mapper.select:
select *
from (
    select rownum rn, a.*
    from (
    select * from eb_item t


            t.brand_id=#{brandId}


            and t.audit_status=#{auditStatus}


            and t.show_status=#{showStatus}


            and t.item_name like '%${itemName}%'


    order by t.item_id desc
    ) a

    where rownum < #{endNum}) b
where b.rn > #{startNum}

select count(*)
from eb_item t


        t.brand_id=#{brandId}


        and t.audit_status=#{auditStatus}


        and t.show_status=#{showStatus}


        and t.item_name like '%${itemName}%'
